import React from "react";


const App = () => {

    const courses = [
    {
        name: 'Half Stack application development',
        id: 1,
        parts: [
            {
            name: 'Fundamentals of React',
            exercises: 10,
            id: 1
            },
            {
            name: 'Using props to pass data',
            exercises: 7,
            id: 2
            },
            {
            name: 'State of component',
            exercises: 14,
            id: 3
            },
            {
            name: 'Redux',
            exercises: 11,
            id: 4
            }
        ]
    },
    {
        name: 'Node.js',
        id: 2,
        parts: [
          {
            name: 'Routing',
            exercises: 3,
            id: 1
          },
          {
            name: 'Middlewares',
            exercises: 7,
            id: 2
          }
        ]
      }
    ]

    return (
        <div>
            {courses.map(course => 
            <Course key={course.id} course={course}/>
            )
        }
        </div>
    )
}

const Course = ({course}) => {
    return (
        <div>
            <Header course = {course}/>
            <Content course = {course}/>
            <Total course={course}/>
        </div>
    )
}

const Header = ({name})=>{
    return (
        <div>
            <h1>
                {name}
            </h1>
        </div>
    )
}

const Content = ({parts})=>{
    return (
        <ul>
        {parts.map(part => 
        <Part key={part.id} part={part.name} exercises={part.exercises}/>)}
        </ul>
    )
}

const Total = (props)=>{
    let sumArr = course.parts.map(exercise => exercise.exercises)
    let sum = sumArr.reduce(function(a,b){return a+b})
    return (
        <div>
            <p>
                <b>Total exercises: {sum}</b>
            </p>
        </div>
    )
}

const Part = ({part}) =>{
    return (
        <li>
            {part.name} {part.exercises}
        </li>
    )

}

export default App;
